(function(){var MTabs=function(params){if(!params)return false;if(params.currentClassName==='undefined')params.currentClassName='current';if(params.eventType==='undefined')params.eventType='mouseover';var _this=this;_this.navObj=document.getElementById(params.nav).getElementsByTagName('ul')[0].getElementsByTagName('li');_this.contentObj=document.getElementById(params.content).getElementsByTagName('div');_this.currentClassName=params.currentClassName;_this.eventType=params.eventType;_this.reset();_this.setClassName(_this.navObj[0].getElementsByTagName('a')[0],_this.currentClassName);_this.setStyle(_this.contentObj[0],'block');_this.action()};MTabs.prototype={action:function(){var _this=this;for(var i=0;i<_this.navObj.length;i++){_this.navObj[i].eq=i;if(_this.eventType=='click'){_this.navObj[i].onclick=function(){_this.reset();_this.setClassName(_this.navObj[this.eq].getElementsByTagName('a')[0],_this.currentClassName);_this.setStyle(_this.contentObj[this.eq],'block')}}else{_this.navObj[i].onmouseover=function(){_this.reset();_this.setClassName(_this.navObj[this.eq].getElementsByTagName('a')[0],_this.currentClassName);_this.setStyle(_this.contentObj[this.eq],'block')}}}},reset:function(){var _this=this;for(var j=0;j<_this.contentObj.length;j++){_this.setClassName(_this.navObj[j].getElementsByTagName('a')[0],'');_this.setStyle(_this.contentObj[j],'none')}},setStyle:function(obj,val){return obj.style.display=val},setClassName:function(obj,val){return obj.className=val}};window.MTabs=MTabs}());